
/**
 * 
 * Calculates the average for a set of data.
 * 
 */
export const average = 'average';

/**
 * The base class for calculating data summaries. The summary is calculated using the
 * method!calculate method. This is overridden in subclasses.
 * 
 */
export const base = 'base';

/**
 * 
 * Calculates the count for a set of data.
 * 
 */
export const count = 'count';

/**
 * 
 * Calculates the maximum for a set of data.
 * 
 */
export const max = 'max';

/**
 * 
 * Calculates the minimum for a set of data.
 * 
 */
export const min = 'min';

/**
 * 
 * Calculates the sum for a set of data.
 * 
 */
export const sum = 'sum';